sys_script_include.script:
var ViaCepService = Class.create();

ViaCepService.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

    getAddressByCEP: function() {
        try {
            var r = new sn_ws.RESTMessageV2();

			r.setEndpoint("https://viacep.com.br/ws/${cep}/json/");
			r.setHttpMethod("get");

			var cep = this.getParameter("sysparm_cep");

            r.setStringParameterNoEscape('cep', cep);

            var response = r.execute();

            return response.getBody();
        } catch (ex) {
            var message = ex.message;
        }
    },
    type: 'ViaCepService'
});
